import React from 'react';
import ReactDOM from 'react-dom';
// 高阶组件
function HOC(WrappedComponent) {
    function wrappedComponentStaic () {
        WrappedComponent.sayHello();
      }
  
      return (props) => (
        <div className={styles.hocWrapper}>
          <WrappedComponent
            inputRef={(el) => { inputElement = el; }}
            {...props}
          />
          /* 省略无关代码... */
          <input
            type="button"
            value="调用子组件static"
            onClick={wrappedComponentStaic}
            className={styles.callButton}
          />
        </div>
      );
  
  }
  
  // 使用
  class Example extends React.Component {
    static sayHello () {
        console.error('hello world'); // tslint:disable-line
      }
    render() {
      return (
      <div>
      <input name="name" {...this.props.name} />
      {this.props.name.value}
      </div>
      )
    }
  }
  
  
const HOCWrap= HOC(Example)
ReactDOM.render(
    <HOCWrap />,
    document.getElementById('root')
  );
